// document loaded
jQuery(function($) {

    var $window = $(window);
    function isAdmin() {
        return document.location.host.indexOf('admin') !== -1;
    }

    function createCookie(name,value) {
        document.cookie = name+"="+value+"; path=/";
    }

    function readCookie(name) {
        var nameEQ = name + "=";
        var ca = document.cookie.split(';');
        for(var i=0;i < ca.length;i++) {
            var c = ca[i];
            while (c.charAt(0)==' ') c = c.substring(1,c.length);
            if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length,c.length);
        }
        return null;
    }

    var sesionCount = readCookie('sesionCounter');
    if (sesionCount===''){
        createCookie('sesionCounter',1);
    }else{
        sesionCount = ++sesionCount;
        createCookie('sesionCounter',sesionCount);
    }
    var dataLayer = window.dataLayer = window.dataLayer || [];
    dataLayer['0']['sesionCount'] = sesionCount;

    (function toggleHamburgerMenu(){
        var $hamburgerMenu = $('.hamburger-menu');
        var isMouseOver=false;
        var $mainHeader = $('header');

        $hamburgerMenu.find('.hamburger-menu-content').mouseover(function(){
            isMouseOver=true;
        });

        $hamburgerMenu.find('.hamburger-menu-icon').click(function() {
            if ( !$('.middle-header').hasClass('active') ) showHideHamburgerMenu($hamburgerMenu);
        });

        toggleItemsIcons();

        $hamburgerMenu.find('.hamburger-menu-content-rendered a').each(function() {
            $(this).mouseover(function () {
                var parentElement = $(this).closest('li').attr('class');
                var categoryType = parentElement.split(" ");
                var categoryElement=".".concat(categoryType[0],"-hamburger-latest-articles");
                $hamburgerMenu.find('.sub-menu-topics').hide();
                $hamburgerMenu.find('.latest-global-articles-hamburger-menu').hide();
                $hamburgerMenu.find('.latest-articles-hamburger-menu').show();
                $hamburgerMenu.find('.latest-articles-hamburger-menu').children().hide();
                $hamburgerMenu.find(categoryElement).show();
            })

        })

        $hamburgerMenu.mouseleave(function () {
                if ($mainHeader.hasClass('hamburger-active')) {
                    $hamburgerMenu.find('.sub-menu-topics').show();
                    $hamburgerMenu.find('.latest-global-articles-hamburger-menu').show();
                    $hamburgerMenu.find('.latest-articles-hamburger-menu').hide();
                    $hamburgerMenu.find('.latest-articles-hamburger-menu').children().hide();
                }else{
                    $hamburgerMenu.find('.sub-menu-topics').hide();
                    $hamburgerMenu.find('.latest-global-articles-hamburger-menu').hide();
                    $hamburgerMenu.find('.latest-articles-hamburger-menu').hide();
                    $hamburgerMenu.find('.latest-articles-hamburger-menu').children().hide();
                }

        })


        $hamburgerMenu.find('.control-icon.fa.fa-plus, .control-icon.fa.fa-minus').click(function (event) {
            event.preventDefault();
            var $this = $(this);
            $this.parent().find('ul').toggle();
            if ($this.hasClass('fa-plus')) {
                $this.removeClass('fa-plus').addClass('fa-minus');
            } else {
                $this.removeClass('fa-minus').addClass('fa-plus');
            }
        })

        $mainHeader.find('.mobile-search-icon').click(function() {
            if ($('header').hasClass('hamburger-active')){
                showHideHamburgerMenu($hamburgerMenu);
            }
        })

        $mainHeader.find('.userlogged').click(function() {
            if ($('header').hasClass('hamburger-active')){
                showHideHamburgerMenu($hamburgerMenu);
            }
            if ($mainHeader.find('.middle-header').hasClass('active')) {
                $mainHeader.find('.search-box-form').hide();
                $mainHeader.find('.middle-header').removeClass('active');
            }
        })


        $(document).on('click', function (e) {
            if (($(e.target).closest($hamburgerMenu).length === 0) && ($mainHeader.hasClass('hamburger-active')) && isMouseOver ) {
                isMouseOver=false;
                showHideHamburgerMenu($hamburgerMenu);
            }
            if (($(e.target).closest($mainHeader.find('.fa-search.mobile-search-icon')).length === 0)
                    && ($(e.target).closest($mainHeader.find('.search-box-form')).length === 0)
                    && ($mainHeader.find('.middle-header').hasClass('active'))) {

                $mainHeader.find('.search-box-form').hide();
                $mainHeader.find('.middle-header').removeClass('active');
            }
        });

        function showHideHamburgerMenu($hamburgerMenu){
            var $header = $('.header-default');
            var $pageContent = $('.page-content');
            var $mainContent = $('.main-container');
            $header.toggleClass('hamburger-active');
            var windowWidth = $(window).width();
            var $menuContent = $hamburgerMenu.find('.hamburger-menu-content-rendered');
            var windowHeight = $(window).height();
            var menuHeight = $menuContent.height();
            var headerHeight = $header.height();
            $hamburgerMenu.find('.latest-global-articles-hamburger-menu').show();
            $hamburgerMenu.find('.latest-articles-hamburger-menu').hide();
            if ($header.hasClass('hamburger-active')) {
                if ((menuHeight + headerHeight + 30) > windowHeight && windowWidth > 969) {
                    $pageContent.hide();
                    $hamburgerMenu.find('.latest-global-articles-hamburger-menu').hide();
                    $hamburgerMenu.find('.latest-articles-hamburger-menu').show();
                } else if ( windowWidth > 969) {
                    $pageContent.show();
                    $hamburgerMenu.find('.latest-global-articles-hamburger-menu').show();
                    $hamburgerMenu.find('.latest-articles-hamburger-menu').hide();
                }
            } else if ( windowWidth > 969 ) {
                $pageContent.show();
                $hamburgerMenu.find('.latest-global-articles-hamburger-menu').show();
                $hamburgerMenu.find('.latest-articles-hamburger-menu').hide();
            }
            $hamburgerMenu.find('.hamburger-menu-icon .bars-ico').toggle();
            $hamburgerMenu.find('.hamburger-menu-icon .fa-times').toggle();
            $pageContent.toggleClass('hamburger-active');
            $mainContent.toggleClass('hamburger-active');
            $(window).resize(function () {
                toggleItemsIcons();
            });
            scrollMenuClick = $(window).scrollTop();

        }
        function toggleItemsIcons() {
            var width = $(window).width();
            if (width < 970) {
                if (!$hamburgerMenu.hasClass('with-controls')) {
                    $hamburgerMenu.addClass('with-controls');
                    $hamburgerMenu.find('.hamburger-menu-content-rendered > ul').each(function () {
                        $(this).children('li').each(function () {
                            var $li = $(this);
                            if (!$li.find('span.control-icon').length) {
                                var iconClasses = 'control-icon';
                                if ($li.hasClass('expandable')) {
                                    $li.find('ul').hide();
                                    iconClasses += ' fa fa-plus';
                                    $li.append('<span class="'+ iconClasses +'"></span>');
                                } else {
                                    iconClasses += ' fa fa-angle-right';
                                    $li.find('a').append('<span class="'+ iconClasses +'"></span>');
                                }

                            }
                        });
                    });
                }
            } else {
                $hamburgerMenu.removeClass('with-controls');
                $hamburgerMenu.find('.control-icon').remove();
                $hamburgerMenu.find('.expandable ul').show();
            }
        }

    })();

    /* hidden submenu elements*/
    var numUpLevel = $("#menu_header_1 .current_ancestor").length;
    var numLevel = $("#menu_header_1 .current").siblings().length;

    if(typeof numMaxElements !== 'undefined' && numMaxElements !== null)
        $("#menu_header_1 .current").siblings().slice( numMaxElements, (numUpLevel+numLevel)).addClass('more-items');

    /* hidden menu elements*/
    $('#more-items-menu').append($('.more-items'));
    $('#menu_header_1').append($('#more-items-menu'));

});

