function _track_share(target, medium) {
    var articleId;

    var $body = $('body');
    var bodyArticleId = $body.data('article-id');
    if (bodyArticleId) {
        //clicked "share" on an article page
        articleId = bodyArticleId;
    } else {
        //clicked "share" in a board
        var $target = $(target);
        var $articleHref = $target.closest('article').find('a').filter(function(idx, a) {
            var $a = $(a);
            var href = $a.attr('href');

            return href && href.match(/\d+$/);
        });

        if ($articleHref.length) {
            var href = $articleHref.attr('href');
            var matches = href.match(/(\d+)$/);
            articleId = matches[1];
        }
    }

    if (articleId) {
        var image = new Image();
        image.src = '/track/shares/article-' + articleId;
    }
}

