            $this.next('span').addClass('invalid');

            if ($this.next('span').hasClass('valid')) {
              $this.next('span').removeClass('valid').addClass('invalid');
            }

            $this.parents('.form-group').find('.invalid-feedback').text($this.data('pattern-pwd-mismatch')).show();
