function switch_toggle(){
  var a = $('#switch .switch').not('.active').first().attr('data-id');
  switch_to(a);
}

function switch_to(elt){
 console.log('altlive: switch to',elt);
  var $s = $('#switch .switch[data-id='+elt+']');
  var a = $('#switch .switch.active').attr('data-id');
  if (elt == a){ return;}
  if (elt == 'live'){
   $('#liveholder').append(ajaxwait()).show();
   $('.altnaslovka, .element').hide();
   $(document).trigger('fin2:ga-send',{
    'hitType':'pageview','page':'/virtual/index/swipe/live'
   });
  }
  else if (elt == 'naslovka'){
   $('#liveholder').hide();
   $('.altnaslovka, .element').show();
   $(document).trigger('fin2:ga-send',{
    'hitType':'pageview','page':'/virtual/index/swipe/news'
   });
  }
  window['_refresh_off'] = (elt == 'live');
  console.log('refresh',window['_refresh_off']);
  $('#switch .switch').removeClass('active');
  $('#switch .switch[data-id='+elt+']').addClass('active');
}

